$.validator.setDefaults({
    highlight: function (e) {
        $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
    }, success: function (e) {
        e.closest(".form-group").removeClass("has-error").addClass("has-success")
    }, errorElement: "span", errorPlacement: function (e, r) {
        e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent().parent() : r.parent())
    }, errorClass: "help-block m-b-none", validClass: "help-block m-b-none"
}),
    $.validator.addMethod("isPhoneNum", function(value, element, param) {
        var length = value.length;
        var mobile = /^(13[0-9]{9})|(18[0-9]{9})|(14[0-9]{9})|(17[0-9]{9})|(15[0-9]{9})$/;
        return this.optional(element) || (length == 11 && mobile.test(value));
    }, "<i class='fa fa-times-circle'></i> 请正确填写您的手机号码"),

    $.validator.addMethod("equalWith", function(value, element, param) {
        return this.optional(element) || value == $(param).val();
    }),
    $().ready(function () {
        var e = "<i class='fa fa-times-circle'></i> ";
        $("#userForm").validate({
            rules: {
                userName: {required: !0, minlength: 2},
                passWord: {minlength: 5},
                confirm_passWord: {minlength: 5, equalWith: "#passWord"},
                email: {required: !0, email: !0},
                telphone: {required: !0, isPhoneNum:!0},
                description: {required: !0, maxlength: 200},
            },
            messages: {
                userName: {required: e + "请输入您的真实姓名", minlength: e + "姓名必须两个字符以上"},
                passWord: {minlength: e + "新密码必须5个字符以上"},
                confirm_passWord: {minlength: e + "新密码必须5个字符以上", equalWith: e + "两次输入的密码不一致"},
                email: e + "请正确输入您的E-mail",
                telphone: {required: e + "请输入您的手机号码"},
                description: {required: e + "请输入描述", maxlength: e + "描述最多199个字符"}
            }
        })
});
